mapper.select:
        select tt.* from (
        select t.*,ifnull((
			select sum(IFNULL(t3.transfer_num,0))
			from t_sale_out_depot t2
			left join t_sale_out_depot_item t3 on t3.sale_out_depot_id = t2.id
			where t2.sale_order_id = t.id and t3.goods_id = t1.goods_id
		),0) as outDepotNum,t1.num
		from t_sale_order t
		left join t_sale_order_item t1 on t.id= t1.order_id
		where t.merchant_id = #{merchantId}

			and t.po_no = #{poNo}

		and t.business_model = '2'
		and t.customer_id = #{customerId}
		and t.state not in ('001','002','006','026')
		and t1.goods_id = #{goodsId}

			and t.id not in (${notInIds})

		) tt
		where tt.num - tt.outDepotNum >= #{totalSalesQty}
		order by create_date
		limit 1

        select tt.* from (
        select t.*,ifnull((
			select sum(IFNULL(t3.transfer_num,0))
			from t_sale_out_depot t2
			left join t_sale_out_depot_item t3 on t3.sale_out_depot_id = t2.id
			where t2.sale_order_id = t.id and t3.goods_id = t1.goods_id
		),0) as outDepotNum,t1.num
		from t_sale_order t
		left join t_sale_order_item t1 on t.id= t1.order_id
		where t.merchant_id = #{merchantId}

			and t.po_no = #{poNo}

		and t.business_model = '2'
		and t.customer_id = #{customerId}
		and t.state not in ('001','002','006','026')
		and t1.goods_id = #{goodsId}
		) tt
		where tt.num - tt.outDepotNum >= #{totalSalesQty}
		order by create_date

    select so.id,so.code,(so.num-ifnull(ott.out_num,0)) as whx_num
    from(
		select t.id,t.code,i.goods_no,sum(i.num) as num
		from t_sale_order t
		left join t_sale_order_item i on t.id=i.order_id
		where t.merchant_id=#{merchantId}
		      and t.out_depot_id=#{depotId}
		      and i.goods_no=#{goodsNo}
		      and business_model='2'
		      and order_source='1'
		      AND t.state NOT IN('001','002','006','026')
		group by t.id,t.code,i.goods_no
	) so
	left join(
		select ot.sale_order_id,oi.goods_no,sum(oi.transfer_num) as out_num
		from t_sale_out_depot ot
		left join t_sale_out_depot_item oi on ot.id=oi.sale_out_depot_id
		where ot.merchant_id=#{merchantId}
			  and ot.out_depot_id=#{depotId}
			  and ot.sale_type='2'
			  and ot.status!='006'
			  and oi.goods_no=#{goodsNo}
	    group by ot.sale_order_id,oi.goods_no
	) ott on so.id=ott.sale_order_id and so.goods_no=ott.goods_no
	where (so.num-IFNULL(ott.out_num,0))>0
	order by so.id

    select i.goods_id,i.goods_no,i.goods_code,i.goods_name,i.barcode,sum(num) as num ,i.commbarcode
	from t_sale_order o
	left join t_sale_order_item i on o.id=i.order_id
	where o.code=#{saleCode}
	      and i.goods_no=#{goodsNo}
	group by i.goods_id,i.goods_no,i.goods_code,i.goods_name,i.barcode,i.commbarcode

