= #{stockLocationTypeId} , stock_location_type_name= #{stockLocationTypeName} ,
id=#{id}

mapper.select:
        select SUM(
              IFNULL(ii.transfer_num,0)+IFNULL(ii.worn_num,0)+IFNULL(ii.expire_num,0)
              ) as inNum
		from t_transfer_in_depot_item ii
		left join t_transfer_in_depot ti on ii.transfer_depot_id=ti.id
		where ti.transfer_order_id in(
		      select id from t_transfer_order t
		      where t.merchant_id=#{merchantId}
		            and t.out_depot_id = #{outDepotId}
		            and t.in_depot_id = #{inDepotId}
		            and t.po_no = #{poNo}
		     )
		     and ti.status = '012'
		     and ii.in_goods_no IN

	         #{goodsNo}

        select sum(
                   IFNULL(di.transfer_num,0)+IFNULL(di.worn_num,0)+IFNULL(di.expire_num,0)
                     ) AS outNum
		from t_transfer_out_depot_item di
		left join t_transfer_out_depot td ON di.transfer_depot_id=td.id
		where td.transfer_order_id IN(
		      select id FROM t_transfer_order t
		      where t.merchant_id=#{merchantId}
		            and t.out_depot_id = #{outDepotId}
		            and t.in_depot_id = #{inDepotId}
		            and t.po_no = #{poNo}
		     )
	        and td.status = '016'
	        and di.out_goods_no IN

		         	#{goodsNo}

