mapper.update:
UPDATE t_purchase_order SET
id= #{id} , code= #{code} , contract_no= #{contractNo} , po_no= #{poNo} , supplier_id= #{supplierId} , supplier_name= #{supplierName} , pay_subject= #{paySubject} , depot_id= #{depotId} , depot_name= #{depotName} , delivery_addr= #{deliveryAddr} , business_model= #{businessModel} , arrive_depot_date= #{arriveDepotDate} , merchant_id= #{merchantId} , merchant_name= #{merchantName} , status= #{status} , ship_date= #{shipDate} , lading_bill= #{ladingBill} , rate= #{rate} , payment_date= #{paymentDate} , load_port= #{loadPort} , unload_port= #{unloadPort} , gross_weight= #{grossWeight} , remark= #{remark} , source= #{source} , is_generate= #{isGenerate} , financing_no= #{financingNo} , financing_remark= #{financingRemark} , end_date= #{endDate} , lbx_no= #{lbxNo} , auditer= #{auditer} , audit_name= #{auditName} , audit_date= #{auditDate} , is_end= #{isEnd} , currency= #{currency} , tgt_currency= #{tgtCurrency} , topideal_code= #{topidealCode} , tallying_unit= #{tallyingUnit} , invoice_name= #{invoiceName} , pay_name= #{payName} , mail_status= #{mailStatus} , pay_date= #{payDate} , invoice_date= #{invoiceDate} , draw_invoice_date= #{drawInvoiceDate} , cargo_cutting_date= #{cargoCuttingDate} , bill_status= #{billStatus} , channel= #{channel} , transport= #{transport} , box_num= #{boxNum} , destination_port_name= #{destinationPortName} , pack_type= #{packType} , pay_rules= #{payRules} , bl_no= #{blNo} , im_exp_port= #{imExpPort} , shipper= #{shipper} , mark= #{mark} , arrive_date= #{arriveDate} , destination_name= #{destinationName} , Invoice_no= #{invoiceNo} , bu_name= #{buName} , bu_id= #{buId} , attribution_date= #{attributionDate} , train_number= #{trainNumber} , standard_case_teu= #{standardCaseTeu} , torr_num= #{torrNum} , carrier= #{carrier} , link_uniue_code= #{linkUniueCode} , amount_adjustment_status= #{amountAdjustmentStatus} , price_increase_rate= #{priceIncreaseRate} , creater= #{creater} , create_name= #{createName} , create_date= #{createDate} , modifier= #{modifier} , modifier_username= #{modifierUsername} , modify_date= #{modifyDate} , amount_adjustmenter= #{amountAdjustmenter} , amount_adjustment_name= #{amountAdjustmentName} , amount_adjustment_date= #{amountAdjustmentDate} , amount_confirm_status= #{amountConfirmStatus} , amount_confirmer= #{amountConfirmer} , amount_confirmer_name= #{amountConfirmerName} , amount_confirmer_date= #{amountConfirmerDate} , submiter= #{submiter} , submiter_name= #{submiterName} , submit_date= #{submitDate} , in_customs_id= #{inCustomsId} , in_customs_code= #{inCustomsCode} , in_platform_customs= #{inPlatformCustoms} , payment_provision= #{paymentProvision} , trade_terms= #{tradeTerms} , frame_contract_no= #{frameContractNo} , approval_no= #{approvalNo} , write_off_status= #{writeOffStatus} , write_off_date= #{writeOffDate} , estimate_deliver_date= #{estimateDeliverDate} , purchase_method= #{purchaseMethod} , audit_method= #{auditMethod} , try_apply_code= #{tryApplyCode} , request_id= #{requestId} , stock_location_type_id= #{stockLocationTypeId} , stock_location_type_name= #{stockLocationTypeName} ,
id=#{id}

mapper.select:
select distinct t.code from t_purchase_order t
left join t_purchase_order_item i on t.id=i.purchase_order_id
where (t.status in('007','006') or t.write_off_status='003') and i.id is not null
 and t.merchant_id = #{merchantId} 

    and (
           (t.create_date>=#{startTime} and t.create_date <=#{endTime})
        or (t.modify_date>=#{startTime} and t.modify_date <=#{endTime})
        or (i.create_date>=#{startTime} and i.create_date <=#{endTime})
        or (i.modify_date>=#{startTime} and i.modify_date <=#{endTime})
    )


    and t.code in

        #{code}


limit #{startIndex},#{pageSize}

select t.code,DATE_FORMAT(t.attribution_date,'%Y-%m-%d %H:%i:%S') as attribution_date,DATE_FORMAT(t.create_date,'%Y-%m-%d %H:%i:%S') as create_date,t.po_no, t.financing_no,t.currency,DATE_FORMAT(t.pay_date,'%Y-%m-%d %H:%i:%S') as pay_date,t.link_uniue_code,t.status,t.write_off_status, DATE_FORMAT(max(w.inbound_date),'%Y-%m-%d %H:%i:%S') as inbound_date,group_concat(w.extra_code) as wmsOrderNo,group_concat(w.contract_no) as contract_no from t_purchase_order t left join t_warehouse_order_rel r on t.id=r.purchase_order_id left join t_purchase_warehouse w on r.warehouse_id=w.id where t.code in
#{code}
group by t.code,t.attribution_date,t.contract_no,t.po_no,t.financing_no,t.currency,t.pay_date,t.link_uniue_code,t.status,t.write_off_status
