mapper.update:
UPDATE t_sale_order SET
id= #{id} , customer_id= #{customerId} , customer_name= #{customerName} , merchant_id= #{merchantId} , merchant_name= #{merchantName} , out_depot_id= #{outDepotId} , out_depot_name= #{outDepotName} , model= #{model} , lbx_no= #{lbxNo} , po_no= #{poNo} , business_model= #{businessModel} , delivery_date= #{deliveryDate} , code= #{code} , state= #{state} , refer_to_order= #{referToOrder} , creater= #{creater} , create_date= #{createDate} , auditor= #{auditor} , audit_date= #{auditDate} , modifier= #{modifier} , modify_date= #{modifyDate} , remark= #{remark} , total_num= #{totalNum} , total_amount= #{totalAmount} , in_depot_id= #{inDepotId} , in_depot_name= #{inDepotName} , create_name= #{createName} , audit_name= #{auditName} , serve_types= #{serveTypes} , contract_no= #{contractNo} , inspect_no= #{inspectNo} , customs_no= #{customsNo} , out_depot_code= #{outDepotCode} , in_depot_code= #{inDepotCode} , topideal_code= #{topidealCode} , is_same_area= #{isSameArea} , end_date= #{endDate} , tallying_unit= #{tallyingUnit} , destination= #{destination} , country= #{country} , receiver_name= #{receiverName} , receiver_address= #{receiverAddress} , mail_mode= #{mailMode} , order_source= #{orderSource} , own_month= #{ownMonth} , original_out_depot_id= #{originalOutDepotId} , original_out_depot_name= #{originalOutDepotName} , original_out_depot_code= #{originalOutDepotCode} , supplier_id= #{supplierId} , supplier_name= #{supplierName} , pay_subject= #{paySubject} , po_date= #{poDate} , currency= #{currency} , modifier_username= #{modifierUsername} , receive_date= #{receiveDate} , receive_name= #{receiveName} , receiver= #{receiver} , bu_name= #{buName} , bu_id= #{buId} , order_type= #{orderType} , pre_sale_order_rel_code= #{preSaleOrderRelCode} , bill_weight= #{billWeight} , transport= #{transport} , teu= #{teu} , trainNo= #{trainno} , carrier= #{carrier} , torus_number= #{torusNumber} , outDepot_addr= #{outdepotAddr} , link_uniue_code= #{linkUniueCode} , amount_status= #{amountStatus} , adjuster= #{adjuster} , adjust_date= #{adjustDate} , adjuster_username= #{adjusterUsername} , amount_confirm_status= #{amountConfirmStatus} , amount_confirmer= #{amountConfirmer} , amount_confirm_username= #{amountConfirmUsername} , amount_confirm_date= #{amountConfirmDate} , pay_rules= #{payRules} , invoice_no= #{invoiceNo} , port_des= #{portDes} , pack= #{pack} , box_num= #{boxNum} , torr_pack_type= #{torrPackType} , out_customs_id= #{outCustomsId} , out_customs_code= #{outCustomsCode} , out_platform_customs= #{outPlatformCustoms} , in_customs_id= #{inCustomsId} , in_customs_code= #{inCustomsCode} , in_platform_customs= #{inPlatformCustoms} , submiter= #{submiter} , submiter_name= #{submiterName} , submit_date= #{submitDate} , finance_status= #{financeStatus} , is_generate_declare= #{isGenerateDeclare} , payment_terms= #{paymentTerms} , trade_terms= #{tradeTerms} , approval_no= #{approvalNo} , write_off_status= #{writeOffStatus} , write_off_date= #{writeOffDate} , stock_location_type_id= #{stockLocationTypeId} , stock_location_type_name= #{stockLocationTypeName} ,
id=#{id}

mapper.select:
select distinct t.code from t_sale_order t
left join t_sale_order_item i on t.id=i.order_id
where (t.state in('018','026','006') or t.write_off_status='003') and i.id is not null
 and t.merchant_id = #{merchantId} 

    and (
           (t.create_date>=#{startTime} and t.create_date <=#{endTime})
        or (t.modify_date>=#{startTime} and t.modify_date <=#{endTime})
        or (i.create_date>=#{startTime} and i.create_date <=#{endTime})
        or (i.modify_date>=#{startTime} and i.modify_date <=#{endTime})
    )


    and t.code in

        #{code}


limit #{startIndex},#{pageSize}

select t.code,DATE_FORMAT(t.audit_date,'%Y-%m-%d %H:%i:%S') as audit_date,DATE_FORMAT(t.create_date,'%Y-%m-%d %H:%i:%S') as create_date, t.contract_no,t.po_no,t.currency,t.state,t.write_off_status,group_concat(d.out_external_code) as wmsOrderNo, max(DATE_FORMAT(d.deliver_date,'%Y-%m-%d %H:%i:%S')) as deliver_date from t_sale_order t left join t_sale_out_depot d on t.id=d.sale_order_id where t.code in
#{code}
group by t.code,t.contract_no,t.po_no,t.currency,t.state,t.write_off_status
